// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./PeggedToken.sol";

contract LPContract {

  //assign Token contract to variable
  PeggedToken private token;

  //add mappings

  mapping(address => uint) public usersBalance;

  //add events
  event Deposit(address indexed user, uint etherAmount, uint timeStart);
  event Withdraw(address indexed user, uint userBalance,  uint amount);

  //pass as constructor argument deployed Token contract
  constructor(PeggedToken _token) {
    //assign token deployed contract to variable
    token = _token;
  }

  function deposit() payable public {
    require(msg.value >= 1e16,'Error, deposit must be >= 0.01 ETH');
    token.transfer(msg.sender, msg.value);
    usersBalance[msg.sender] +=msg.value;
    //emit Deposit event
    emit Deposit(msg.sender,msg.value,block.timestamp);
  }

  function withdraw(uint256 amount) public {
    uint userBalance = usersBalance[msg.sender];
    //send eth to user
    token.transferFrom(msg.sender, address(this) ,amount );
    //send interest in tokens to user
    usersBalance[msg.sender] -= amount;
    //emit event
    emit Withdraw(msg.sender,userBalance,amount);
  }

}
